cycle_array() {
  local -n arr=$1
  local -n index=$2
  local var_name=$3
  index=$(( (index + 1) % ${#arr[@]} ))
  eval "$var_name=\"\${arr[$index]}\""
  message="$4: ${!var_name}"
}

toggle_option() {
  local var_name=$1
  local label=$2
  local current
  local new_value
  local status

  current=$(eval "echo \$$var_name")

  if [ "$current" == "True" ]; then
    new_value="False"
    status="Disabled"
  else
    new_value="True"
    status="Enabled"
  fi

  eval "$var_name=\"$new_value\""
  message="$label $status"
}

